package com.roc.cloud.db.entity;import java.io.Serializable;import java.util.ArrayList;import java.util.List;import lombok.Data;import org.apache.commons.collections4.CollectionUtils;/** * @ClassName: TenantDataSourceInfo * @Description: 租户数据源信息 * @author: Roc * @date: 2020/9/24 15:30 **/@Datapublic class TenantDataSourceInfo implements Serializable {    private static final long serialVersionUID = -1737195212866224974L;    /**     * 租户信息     */    private String tenant;    private String appName;    private DataSourceInfo masterDataSource;    private List<DataSourceInfo> slaveDataSourceList;    public void addSlaveDataSourceList(DataSourceInfo slaveDataSource) {        if (CollectionUtils.isEmpty(slaveDataSourceList)) {            slaveDataSourceList = new ArrayList<>();        }        slaveDataSourceList.add(slaveDataSource);    }}