package com.roc.cloud.db.config;import com.baomidou.mybatisplus.core.handlers.MetaObjectHandler;import com.baomidou.mybatisplus.extension.plugins.OptimisticLockerInterceptor;import com.roc.cloud.db.handler.DateMetaObjectHandler;import com.roc.cloud.db.properties.MybatisPlusAutoFillProperties;import lombok.extern.slf4j.Slf4j;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.boot.autoconfigure.condition.ConditionalOnMissingBean;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.ComponentScan;/** * @ClassName: DynamicMybatisConfiguration * @Description: 创建、更新时间字段自动填充, * 目前此功能不用，用mysql自带的能力来实现 * @author: Roc * @date: 2020/9/9 13:55 **/@Slf4j@ComponentScan(basePackages = "com.roc.cloud.database")public class FillMybatisConfiguration {    @Autowired    MybatisPlusAutoFillProperties autoFillProperties;    @Bean    @ConditionalOnMissingBean    public MetaObjectHandler metaObjectHandler() {        return new DateMetaObjectHandler(autoFillProperties);    }    @Bean    public OptimisticLockerInterceptor optimisticLockerInterceptor() {        return new OptimisticLockerInterceptor();    }}