package com.roc.cloud.security.auth.service;import com.nimbusds.jose.JOSEException;import com.nimbusds.jose.jwk.RSAKey;import com.roc.cloudsecurity.auth.api.PayloadDto;import java.text.ParseException;/** * JWT-token管理器 <br> * * @date: 2020/10/14 <br> * @author: Roc <br> * @since: 1.0 <br> * @version: 1.0 <br> */public interface JwtTokenService {    /**     * 获取默认payload     *     * @return     * @author Roc     * @date 2020/10/16     **/    PayloadDto getDefaultPayloadDto();    /**     * 获取payload，指定过期时间     *     * @param expired 过期时间（分钟）     * @return     * @author Roc     * @date 2020/10/16     **/    PayloadDto getDefaultPayloadDto(int expired);    /**     * 使用HMAC算法生成token     *     * @param payloadStr :     * @param secret     :     * @return java.lang.String     * @throws JOSEException     * @author Roc     * @date 2020/10/16     **/    String generateTokenByHmac(String payloadStr, String secret) throws JOSEException;    /**     * 使用HMAC算法校验token     *     * @param token  :     * @param secret :     * @return     * @throws ParseException     * @throws JOSEException     * @author Roc     * @date 2020/10/16     **/    PayloadDto verifyTokenByHmac(String token, String secret) throws ParseException, JOSEException;    /**     * 使用RSA算法生成token     *     * @param payloadStr :     * @param rsaKey     :     * @return java.lang.String     * @throws JOSEException     * @author Roc     * @date 2020/10/16     **/    String generateTokenByRsa(String payloadStr, RSAKey rsaKey) throws JOSEException;    /**     * 使用RSA算法校验token     *     * @param token  :     * @param rsaKey :     * @return     * @throws ParseException     * @throws JOSEException     * @author Roc     * @date 2020/10/16     **/    PayloadDto verifyTokenByRsa(String token, RSAKey rsaKey) throws ParseException, JOSEException;    /**     * 获取默认的RSAKey     *     * @return java.security.interfaces.RSAKey     * @author Roc     * @date 2020/10/16     **/    RSAKey getDefaultRsaKey();}